    [Trait("Type", "Unit")]
    public void Encode_TestCases_ReturnCorrectStrings(string bytes, string base32)
    {
        // Tested against some random Wily.Base32 nuget package output
        var b = bytes.ToHexBytes();
        Assert.Equal(base32, Base32.Encode(b, true));
        Assert.Equal(base32.TrimEnd('='), Base32.Encode(b, false));
    }
